import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Modal from "./modal";
import styles from "./player2.module.css";
import RestartModal from "./restartModal";
const DUMMY_DATA = [
  {
    object: "Apple",
    category: "Fruit",
    description: "Red in color and mostly cultivated in Jammu & Kashmir",
  },
  {
    object: "Car",
    category: "Vehicle",
    description: "Four wheeler Vehicle, use for the personal uses mostly.",
  },
  {
    object: "Lion",
    category: "Animal",
    description: "King of the jungle, brown in color",
  },
  {
    object: "Banana",
    category: "Fruit",
    description: "Yellow in color.It is cultivated without the use of seeds",
  },
  {
    object: "Bike",
    category: "Vehicle",
    description: "Two wheeler, and worked with the help of motor",
  },
  {
    object: "Tiger",
    category: "Animal",
    description: "A wild animal,having strips in its body",
  },
  {
    object: "Bus",
    category: "Vehicle",
    description:
      "Large vehicle for transportation,mainly uses for public transportation",
  },
  {
    object: "Grapes",
    category: "Fruit",
    description: "Purple in color, also present in green color.",
  },
  {
    object: "Motorcycle",
    category: "Vehicle",
    description: "Two wheeler with an engine and extended version of cycles.",
  },
  {
    object: "Elephant",
    category: "Animal",
    description: "Largest land mammal",
  },
  {
    object: "Watermelon",
    category: "Fruit",
    description: "Red and juicy, and outer covering is in green color.",
  },
  {
    object: "Truck",
    category: "Vehicle",
    description:
      "Large vehicle for transportation,not for public but specially goods transportation.",
  },
  {
    object: "Giraffe",
    category: "Animal",
    description: "Tallest land mammal",
  },
];
export default function Player2({
  dataReceive,
  dataReceive2,
  dataReceive3,
  attempts,
  player1Name,
  player2Name,
  isRobot,
  playingChoiceImg,
}) {
  const [finalAttempts, setFinalAttempts] = useState(attempts);
  const [guess, setGuess] = useState("");
  const [guess2, setGuess2] = useState("");
  const [guess3, setGuess3] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [modal, setModal] = useState(false);
  const [score, setScore] = useState("0");
  const [shakeAttempts, setShakeAttempts] = useState(false);
  const [able, setAble] = useState({
    able1: false,
    able2: false,
    able3: false,
  });
  const [dataReceived, setDataReceived] = useState(dataReceive);
  const [dataReceived2, setDataReceived2] = useState(dataReceive2);
  const [dataReceived3, setDataReceived3] = useState(dataReceive3);
  const [restartModal, setRestartModal] = useState(false);
  const [runUseEffectAgain, setRunUseEffectAgain] = useState(false);
  const [image, setImage] = useState(playingChoiceImg);
  const navigate = useNavigate();
  useEffect(() => {
    if (isRobot) {
      setDataReceived(
        DUMMY_DATA[Math.floor(Math.random() * DUMMY_DATA.length)]
      );
      setDataReceived2(
        DUMMY_DATA[Math.floor(Math.random() * DUMMY_DATA.length)]
      );
      setDataReceived3(
        DUMMY_DATA[Math.floor(Math.random() * DUMMY_DATA.length)]
      );
      setImage(DUMMY_DATA[Math.floor(Math.random() * DUMMY_DATA.length)]);
    }
  }, [runUseEffectAgain, isRobot]);
  function handleGuess(event) {
    setGuess(event.target.value);
  }
  function handleGuess2(event) {
    setGuess2(event.target.value);
  }
  function handleGuess3(event) {
    setGuess3(event.target.value);
  }
  function handleSubmit(event) {
    event.preventDefault();
    if (guess.toLowerCase() !== dataReceived.object.toLowerCase()) {
      setFinalAttempts((finalAttempts) => finalAttempts - 1);
      setIsSubmitted(false);
      setShakeAttempts(true);
      setTimeout(() => setShakeAttempts(false), 500);
    } else if (guess.toLowerCase() === dataReceived.object.toLowerCase()) {
      setFinalAttempts((finalAttempts) => finalAttempts - -3);
      setIsSubmitted(true);
      setAble((prevState) => ({ ...prevState, able1: true }));
      setScore(Math.round((finalAttempts * 100) / attempts));
    }
    console.log(finalAttempts);
    if (
      attempts > 8 &&
      attempts <= 12 &&
      guess.toLowerCase() === dataReceived.object.toLowerCase()
    ) {
      setModal(true);
    }
    if (
      attempts > 3 &&
      attempts <= 8 &&
      guess.toLowerCase() === dataReceived.object.toLowerCase() &&
      guess2.toLowerCase() === dataReceived2.object.toLowerCase()
    ) {
      setModal(true);
    }
    if (
      attempts <= 3 &&
      guess.toLowerCase() === dataReceived.object.toLowerCase() &&
      guess2.toLowerCase() === dataReceived2.object.toLowerCase() &&
      guess3.toLowerCase() === dataReceived3.object.toLowerCase()
    ) {
      setModal(true);
    }
  }

  function handleSubmit2(event) {
    event.preventDefault();
    if (guess2.toLowerCase() !== dataReceived2.object.toLowerCase()) {
      setFinalAttempts((finalAttempts) => finalAttempts - 1);
      setIsSubmitted(false);
      setShakeAttempts(true);
      setTimeout(() => setShakeAttempts(false), 500);
    } else if (guess2.toLowerCase() === dataReceived2.object.toLowerCase()) {
      setFinalAttempts((finalAttempts) => finalAttempts - -3);
      setIsSubmitted(true);
      setAble((prevState) => ({ ...prevState, able2: true }));
      setScore(Math.round((finalAttempts * 100) / attempts));
    }
    console.log(finalAttempts);
    if (
      attempts > 8 &&
      attempts <= 12 &&
      guess.toLowerCase() === dataReceived.object.toLowerCase()
    ) {
      setModal(true);
    }
    if (
      attempts > 3 &&
      attempts <= 8 &&
      guess.toLowerCase() === dataReceived.object.toLowerCase() &&
      guess2.toLowerCase() === dataReceived2.object.toLowerCase()
    ) {
      setModal(true);
    } else if (
      attempts <= 3 &&
      guess.toLowerCase() === dataReceived.object.toLowerCase() &&
      guess2.toLowerCase() === dataReceived2.object.toLowerCase() &&
      guess3.toLowerCase() === dataReceived3.object.toLowerCase()
    ) {
      setModal(true);
    }
  }
  function handleSubmit3(event) {
    event.preventDefault();
    if (guess3.toLowerCase() !== dataReceived3.object.toLowerCase()) {
      setFinalAttempts((finalAttempts) => finalAttempts - 1);
      setIsSubmitted(false);
      setShakeAttempts(true);
      setTimeout(() => setShakeAttempts(false), 500);
    } else if (guess3.toLowerCase() === dataReceived3.object.toLowerCase()) {
      setFinalAttempts((finalAttempts) => finalAttempts - -3);
      setIsSubmitted(true);
      setAble((prevState) => ({ ...prevState, able3: true }));
      setScore(Math.round((finalAttempts * 100) / attempts));
    }
    console.log(finalAttempts);
    if (
      attempts > 8 &&
      attempts <= 12 &&
      guess.toLowerCase() === dataReceived.object.toLowerCase()
    ) {
      setModal(true);
    } else if (
      attempts > 3 &&
      attempts <= 8 &&
      guess.toLowerCase() === dataReceived.object.toLowerCase() &&
      guess2.toLowerCase() === dataReceived2.object.toLowerCase()
    ) {
      setModal(true);
    } else if (
      attempts <= 3 &&
      guess.toLowerCase() === dataReceived.object.toLowerCase() &&
      guess2.toLowerCase() === dataReceived2.object.toLowerCase() &&
      guess3.toLowerCase() === dataReceived3.object.toLowerCase()
    ) {
      setModal(true);
    }
  }
  function handleBack() {
    if (!isRobot) navigate("/player1");
    else navigate("/levels");
  }
  function handleRestart() {
    setRestartModal(true);
  }
  console.log(restartModal);

  const resethandler = () => {
    setRunUseEffectAgain((prev) => !prev);
    setFinalAttempts(attempts);
    setGuess("");
    setGuess2("");
    setGuess3("");
    setAble({
      able1: false,
      able2: false,
      able3: false,
    });
  };
  // console.log(finalAttempts);
  console.log(modal);
  return (
    <div className={styles.mainContainer}>
      <button className={styles.backButton} onClick={handleBack}>
        Back
      </button>
      <button
        className={styles.restartButton}
        onClick={handleRestart}
        disabled={
          modal ||
          (finalAttempts === 0 &&
            ((attempts <= 3 &&
              ((guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                guess2.toLowerCase() !== dataReceived2.object.toLowerCase() &&
                guess3.toLowerCase() !== dataReceived3.object.toLowerCase()) ||
                (guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                  guess2.toLowerCase() !== dataReceived2.object.toLowerCase() &&
                  guess3.toLowerCase() ===
                    dataReceived3.object.toLowerCase()) ||
                (guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                  guess2.toLowerCase() === dataReceived2.object.toLowerCase() &&
                  guess3.toLowerCase() !==
                    dataReceived3.object.toLowerCase()) ||
                (guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                  guess2.toLowerCase() === dataReceived2.object.toLowerCase() &&
                  guess3.toLowerCase() ===
                    dataReceived3.object.toLowerCase()) ||
                (guess.toLowerCase() === dataReceived.object.toLowerCase() &&
                  guess2.toLowerCase() !== dataReceived2.object.toLowerCase() &&
                  guess3.toLowerCase() !==
                    dataReceived3.object.toLowerCase()) ||
                (guess.toLowerCase() === dataReceived.object.toLowerCase() &&
                  guess2.toLowerCase() !== dataReceived2.object.toLowerCase() &&
                  guess3.toLowerCase() ===
                    dataReceived3.object.toLowerCase()) ||
                (guess.toLowerCase() === dataReceived.object.toLowerCase() &&
                  guess2.toLowerCase() === dataReceived2.object.toLowerCase() &&
                  guess3.toLowerCase() !==
                    dataReceived3.object.toLowerCase()))) ||
              (attempts > 3 &&
                attempts <= 8 &&
                ((guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                  guess2.toLowerCase() !==
                    dataReceived2.object.toLowerCase()) ||
                  (guess.toLowerCase() === dataReceived.object.toLowerCase() &&
                    guess2.toLowerCase() !==
                      dataReceived2.object.toLowerCase()) ||
                  (guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                    guess2.toLowerCase() ===
                      dataReceived2.object.toLowerCase()))) ||
              (attempts > 8 &&
                attempts <= 12 &&
                guess.toLowerCase() !== dataReceived.object.toLowerCase())))
        }
      >
        Restart
      </button>
      <div
        className={styles.playerContainer}
        style={{
          opacity:
            modal ||
            (finalAttempts === 0 &&
              ((attempts <= 3 &&
                ((guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                  guess2.toLowerCase() !== dataReceived2.object.toLowerCase() &&
                  guess3.toLowerCase() !==
                    dataReceived3.object.toLowerCase()) ||
                  (guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                    guess2.toLowerCase() !==
                      dataReceived2.object.toLowerCase() &&
                    guess3.toLowerCase() ===
                      dataReceived3.object.toLowerCase()) ||
                  (guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                    guess2.toLowerCase() ===
                      dataReceived2.object.toLowerCase() &&
                    guess3.toLowerCase() !==
                      dataReceived3.object.toLowerCase()) ||
                  (guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                    guess2.toLowerCase() ===
                      dataReceived2.object.toLowerCase() &&
                    guess3.toLowerCase() ===
                      dataReceived3.object.toLowerCase()) ||
                  (guess.toLowerCase() === dataReceived.object.toLowerCase() &&
                    guess2.toLowerCase() !==
                      dataReceived2.object.toLowerCase() &&
                    guess3.toLowerCase() !==
                      dataReceived3.object.toLowerCase()) ||
                  (guess.toLowerCase() === dataReceived.object.toLowerCase() &&
                    guess2.toLowerCase() !==
                      dataReceived2.object.toLowerCase() &&
                    guess3.toLowerCase() ===
                      dataReceived3.object.toLowerCase()) ||
                  (guess.toLowerCase() === dataReceived.object.toLowerCase() &&
                    guess2.toLowerCase() ===
                      dataReceived2.object.toLowerCase() &&
                    guess3.toLowerCase() !==
                      dataReceived3.object.toLowerCase()))) ||
                (attempts > 3 &&
                  attempts <= 8 &&
                  ((guess.toLowerCase() !== dataReceived.object.toLowerCase() &&
                    guess2.toLowerCase() !==
                      dataReceived2.object.toLowerCase()) ||
                    (guess.toLowerCase() ===
                      dataReceived.object.toLowerCase() &&
                      guess2.toLowerCase() !==
                        dataReceived2.object.toLowerCase()) ||
                    (guess.toLowerCase() !==
                      dataReceived.object.toLowerCase() &&
                      guess2.toLowerCase() ===
