import style from "./modal.module.css";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
export default function Modal({
  message,
  leftAttempts,
  finalScore,
  object1,
  object2,
  object3,
}) {
  const navigate = useNavigate();
  const [win, setWin] = useState(true);
  if (object1 === "" && object2 === "" && object3 === "") {
    setWin(false);
  }
  function handleRestart() {
    navigate("/player1");
  }
  function handleLevel() {
    navigate("/levels");
  }
  return (
    <dialog open className={style.modal}>
      <h1
        className={style.h1}
        style={{ color: message === "You Won" ? "green" : "red" }}
      >
        {message}
      </h1>
      {win && <p className={style.p}>Guess 1: {object1}</p>}
      {win && object2 !== "" && <p className={style.p}>Guess 2: {object2}</p>}
      {win && object2 !== "" && <p className={style.p}>Guess 3: {object3}</p>}
      <p className={style.p}>Attempts Left:{leftAttempts}</p>
      <p className={style.p}>Score:{finalScore}</p>
      <button onClick={handleLevel} className={style.button}>
        Level
      </button>
      <button onClick={handleRestart} className={style.button}>
        Restart
      </button>
    </dialog>
  );
}
