import style from "./playingOption.module.css";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
export default function PlayingOption({ checkOption }) {
  const [innerContainerClass, setInnerContainerClass] = useState("");
  const navigate = useNavigate();
  function handleClick1() {
    checkOption(true);
    // const newClass = innerContainerClass === "" ? "rotated" : "";
    // setInnerContainerClass(newClass);
    // console.log(newClass);
    // navigate("/levels");
  }
  function handleClick2() {
    checkOption(false);
    navigate("/levels");
  }
  function handleBack() {
    navigate("/");
  }
  console.log(innerContainerClass);
  return (
    <div className={style.playingOption}>
      <button className={style.backButton} onClick={handleBack}>
        Back
      </button>
      <div
        className={
          innerContainerClass === ""
            ? `${style.innerContainer}`
            : `${style.rotated}`
        }
      >
        <h1 className={style.h1}>Playing Options</h1>
        <button className={style.button} onClick={handleClick1}>
          Single Player
        </button>
        <button className={style.button} onClick={handleClick2}>
          Multi Player
        </button>
      </div>
    </div>
  );
}
